INSERT INTO `Kategorie_Tab_Text` VALUES (3,'Songs','English Songs','Hakuna Mungu / There´s no one','Hakuna Mungu kama wewe<br>Hakuna Mungu kama wee<br>Hakuna Mungu kama wewe<br>Hakuna na hatakuwepo<br><br>Nimetembea kote kote<br>Nimezunguka kote kote<br>Nimetafuta kote kote<br>Hakuna na hatakuwepo<br><br><br>There''s no one, there''s no one like Jesus<br>There''s no one, there''s no one like him<br>There''s no one, there''s no one like Jesus<br>There''s no one, there''s no one like him<br><br>I''ve walked and walked all over, over<br>I turned and turned all over, over<br>I''ve searched and searched all over, over<br>There''s no one, there''s no one like him',0,0);
INSERT INTO `Kategorie_Tab_Text` VALUES (4,'Songs','Englische Lieder','Hakuna Mungu / There´s no one','Hakuna Mungu kama wewe<br>Hakuna Mungu kama wee<br>Hakuna Mungu kama wewe<br>Hakuna na hatakuwepo<br><br>Nimetembea kote kote<br>Nimezunguka kote kote<br>Nimetafuta kote kote<br>Hakuna na hatakuwepo<br><br><br>There''s no one, there''s no one like Jesus<br>There''s no one, there''s no one like him<br>There''s no one, there''s no one like Jesus<br>There''s no one, there''s no one like him<br><br>I''ve walked and walked all over, over<br>I turned and turned all over, over<br>I''ve searched and searched all over, over<br>There''s no one, there''s no one like him',0,0);
INSERT INTO `Kategorie_Tab_Text` VALUES (4,'Night Prayer','Night Prayer','Our Father','Vater unser im Himmel<br>Geheiligt werde dein Name.<br>Dein Reich komme.<br>Dein Wille geschehe,<br>wie im Himmel, so auf Erden.<br>Unser tägliches Brot gib uns heute.<br>Und vergib uns unsere Schuld,<br>wie auch wir vergeben unsern Schuldigern.<br>Und führe uns nicht in Versuchung,<br>sondern erlöse uns von dem Bösen.<br>Denn dein ist das Reich<br>und die Kraft und die Herrlichkeit<br>in Ewigkeit. Amen.',0,1);
INSERT INTO `Kategorie_Tab_Text` VALUES (3,'Night Prayer','Night Prayer','Our father','Our Father in heaven,<br><br>hallowed be your name,<br><br>your kingdom come,<br><br>your will be done,<br><br>on earth as in heaven.<br><br>Give us today our daily bread.<br><br>Forgive us our sins<br><br>as we forgive those who sin against us.<br><br>Save us from the time of trial<br><br>and deliver us from evil.<br><br>For the kingdom, the power, and the glory are yours<br><br>now and forever. Amen.',0,1);
