INSERT INTO `Kategorie_Tab_Text` VALUES (4,'Workshops','Musik','Chor','Di1, Mi1, Fr1, Sa1',0,0);
INSERT INTO `Kategorie_Tab_Text` VALUES (4,'Workshops','Musik','Chor','Du hast Lust zu singen? Dann ist alles was du jetzt noch brauchst deine Stimme – die Musik dazu machen wir gemeinsam. Wir singen überwiegend a cappella, und bedienen uns aus dem Fundus der aktuellen Pop- und Worshipsongs! Ziel ist es, dabei so viel Spaß wie möglich zu haben! ',0,1);
