INSERT INTO `Kategorie_Tab_Text` VALUES (4,'Workshops','Themen-Workshops','Mein Weg mit Jesus','Mein Weg mit Jesus - Ein erlebnispädagogischer Parcours zu Situationen des Lebens, eine aktive Erfahrung für den Einzelnen und die Gruppe.',0,1);
INSERT INTO `Kategorie_Tab_Text` VALUES (4,'Workshops','Themen-Workshops','Mein Weg mit Jesus','Leitung: Günter Lücking<br>Ort: Pavillon 2<br>Teilnehmer: 25<br>Bitte mitbringen: -<br>Tage: Fr',0,2);
