CREATE TABLE IF NOT EXISTS "Veranstaltung" (	"Id"	INTEGER,	"Kürzel"	TEXT NOT NULL,	"Name"	TEXT NOT NULL,	"Sprache"	TEXT NOT NULL,	"Jahr"	INTEGER NOT NULL,	"Dauer"	INTEGER,	PRIMARY KEY("Id"));
CREATE TABLE IF NOT EXISTS "Instanz" (	"Id_Veranstaltung"	INTEGER,	"id"	INTEGER,	"StartDatum"	TEXT NOT NULL,	PRIMARY KEY("Id_Veranstaltung","id"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES Veranstaltung(Id));
CREATE TABLE IF NOT EXISTS "Beschreibung" (	"Id_Veranstaltung"	INTEGER,	"Id_Instanz"	INTEGER,	"Termin_Tag"	INTEGER NOT NULL,	"Termin_Uhrzeit"	TEXT,	"Text"	TEXT NOT NULL,	"Typ"	INTEGER DEFAULT 1,	PRIMARY KEY("Id_Veranstaltung","Id_Instanz","Termin_Tag","Termin_Uhrzeit","Text"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES "Veranstaltung"("Id"),	FOREIGN KEY("Id_Instanz") REFERENCES "Instanz"("Id"),	FOREIGN KEY("Termin_Tag") REFERENCES "Termin"("Tag"),	FOREIGN KEY("Termin_Uhrzeit") REFERENCES "Termin"("Uhrzeit"));
CREATE TABLE IF NOT EXISTS "Termin" (	"Id_Veranstaltung"	INTEGER,	"Tag"	INTEGER NOT NULL,	"Uhrzeit"	TEXT NOT NULL,	"Titel"	TEXT NOT NULL,	"Typ"	INTEGER DEFAULT 0,	PRIMARY KEY("Id_Veranstaltung","Tag","Uhrzeit"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES "Veranstaltungen"("Id"));
CREATE TABLE IF NOT EXISTS "Kategorie" (	"Id_Veranstaltung"	INTEGER,	"Titel"	TEXT,	PRIMARY KEY("Id_Veranstaltung","Titel"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES Veranstaltung(Id));
CREATE TABLE IF NOT EXISTS "Kategorie_Tab_Titel" (	"Id_Veranstaltung"	INTEGER,	"Name_Kategorie"	TEXT,	"TabName_Kategorie_Tab"	TEXT,	"Titel"	TEXT,	"Typ"	INTEGER NOT NULL,	"Rang"	INTEGER NOT NULL,	PRIMARY KEY("Id_Veranstaltung","Name_Kategorie","TabName_Kategorie_Tab","Titel","Rang"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES "Veranstaltung"("Id"),	FOREIGN KEY("Name_Kategorie") REFERENCES "Kategorie"("Name"),	FOREIGN KEY("TabName_Kategorie_Tab") REFERENCES "Kategorie_Tab"("TabName"));
CREATE TABLE IF NOT EXISTS "Kategorie_Tab_Text" (	"Id_Veranstaltung"	INTEGER NOT NULL,	"Name_Kategorie"	TEXT NOT NULL,	"TabName_Kategorie_Tab"	TEXT NOT NULL,	"Titel_Kategorie_Tab_Titel"	TEXT NOT NULL,	"Text"	TEXT NOT NULL,	"Typ"	INTEGER NOT NULL,	"Rang"	INTEGER NOT NULL,	PRIMARY KEY("Id_Veranstaltung","Name_Kategorie","TabName_Kategorie_Tab","Titel_Kategorie_Tab_Titel","Text","Typ","Rang"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES "Veranstaltung"("Id"),	FOREIGN KEY("Name_Kategorie") REFERENCES "Kategorie"("Name"),	FOREIGN KEY("TabName_Kategorie_Tab") REFERENCES "Kategorie_Tab"("TabName"),	FOREIGN KEY("Titel_Kategorie_Tab_Titel") REFERENCES "Kategorie_Tab_Inhalt"("Titel"));
CREATE TABLE IF NOT EXISTS "Kategorie_Tab" (	"Id_Veranstaltung"	INTEGER,	"Name_Kategorie"	TEXT,	"TabName"	TEXT,	"Rang"	INTEGER,	PRIMARY KEY("Id_Veranstaltung","Name_Kategorie","TabName","Rang"),	FOREIGN KEY("Id_Veranstaltung") REFERENCES "Veranstaltung"("Id"),	FOREIGN KEY("Name_Kategorie") REFERENCES "Kategorie"("Name"));
